const n=JSON.parse('{"key":"v-2ba80b07","path":"/note/db/redis/2305091740.html","title":"Redis哨兵","lang":"zh-CN","frontmatter":{"isOriginal":true,"date":"2021-01-18T00:00:00.000Z","index":true,"order":5,"category":["DB"],"tag":["Redis"]},"headers":[{"level":2,"title":"搭建","slug":"搭建","link":"#搭建","children":[]},{"level":2,"title":"工作原理","slug":"工作原理","link":"#工作原理","children":[]},{"level":2,"title":"优缺点","slug":"优缺点","link":"#优缺点","children":[]}],"git":{"createdTime":1686368450000,"updatedTime":1686368450000,"contributors":[{"name":"yanggl","email":"2549597630@qq.com","commits":1}]},"readingTime":{"minutes":1.79,"words":537},"filePathRelative":"note/db/redis/2305091740.md","localizedDate":"2021年1月18日","excerpt":"<h1> Redis哨兵</h1>\\n<h2> 搭建</h2>\\n<ol>\\n<li>复制一份 sentinel.conf 文件</li>\\n<li>将相关配置修改为如下值：</li>\\n</ol>\\n<div class=\\"language-bash\\" data-ext=\\"sh\\"><pre class=\\"language-bash\\"><code>port <span class=\\"token number\\">26379</span>\\ndaemonsize <span class=\\"token function\\">yes</span>\\npidfile <span class=\\"token string\\">\\"/var/run/redis-sentinel-26379.pid\\"</span>\\nlogfile <span class=\\"token string\\">\\"26379.log\\"</span>\\n<span class=\\"token function\\">dir</span> <span class=\\"token string\\">\\"/usr/local/redis-5.0/data\\"</span>\\n\\n<span class=\\"token comment\\"># sentinel monitor &lt; master - name &gt; &lt; ip &gt; &lt; redis - port &gt; &lt; quorum &gt;</span>\\n<span class=\\"token comment\\"># quorum是一个数字，指明当有多少个sentinel认为一个master失效时（值一般为：sentinel / 2 + 1），master才算真正失效</span>\\nsentinel monitor mymaster <span class=\\"token number\\">192.168</span>.0.1 <span class=\\"token number\\">6379</span> <span class=\\"token number\\">2</span>\\n</code></pre></div>"}');export{n as data};
